import React, { useState } from 'react';
import thumbNail from '../images/image-product-1-thumbnail.jpg'
import DelIcon from '../images/icon-delete.svg'


const CartItem = ({count, setCounter, finalCheckout, shoename, price, discount, id, delCartItem}) => {
    const finalCheck = () => {
        setCounter(0)
        
      };
    
     return <div key={id} className='absolute z-50 left-4 right-8 md:right-20 h-56 bg-White md:left-[650px] p-5 shadow max-w-full md-w-64 divide-y'>
      <h2 className=''>Cart</h2>
      {count > 0 ? <div className='flex flex-col justify-evenly p-2'>
      <div className='flex'>
        <img className='h-16 w-12 rounded p-1' src={thumbNail} alt="" />
        <div className="text-Grayishblue">
                <p>{shoename}</p>
                <p>{discount/100 * price} x {count} <span>{(discount/100 * price) * count}</span> <span>{'$'}</span></p>
            </div>
            <div className='flex justify-center items-center p-1'>
            <button onClick={delCartItem} >
                <img src={DelIcon} alt="" />
            </button>
            </div>
      </div>
      <button onClick={finalCheck} className=' h-10 text-center cursor-pointer active:bg-Paleorange  shadow p-2 w-4/5 bg-Orange rounded text-White hover:bg-red cursor-pointer font-bold font-press-start'>Checkout</button>
      </div> : <div className='flex justify-center items-center p-16
        '><p className='text-center max-w-full'>Your Cart is Empty</p></div>  }
  </div>;
};

export default CartItem;
