import {React, useState} from 'react';
import { Link } from 'react-router-dom';
import MenuBar from '../images/icon-menu.svg'
import MenuBarClose from '../images/icon-close.svg'

import cartIcon from '../images/icon-cart.svg'
import userIcon from '../images/image-avatar.png'
import CartModal from './Cart';

 
function Nav({count, setCounter}) {
    const [showModal, setShowModal] = useState(false)

    const handleModal = () => {
        setShowModal(prev => !prev)
    }

    const [click, setClick] = useState(false)

    const handleClick = () => {
        setClick(!click)
    }
   
  return <div className='relative divide-y'>
      <SideNav />
      
        <nav className='flex justify-between items-center m-auto w-4/5 h-15'>

             <div onClick={handleClick} className='menu-bar md:hidden'> 
                <img src={click ? MenuBarClose : MenuBar} alt="" />
            </div>
            
          <div className="logo basis-1/4">
              <Link className='font-bold text-2xl' to='/'>
                  sneakers
              </Link>
          </div>

          <div className="navlinks flex basis-3/4 justify-evenly md:block hidden">
              <Link to='/' className='p-4'>Collections</Link>
              <Link to='/men' className='p-4' >Men</Link>
              <Link to='/women' className='p-4' >Women</Link>
              <Link to='/children' className='p-4'>Children</Link>
              <Link to='/about' className='p-4'>About</Link>
              <Link to='/contact' className='p-4'>Contact</Link>


          </div>
            <div className="cart flex items-center ">
                <div className="cart-icon p-3 relative">
                
                        <img className='cursor-pointer' onClick={handleModal} src={cartIcon} alt="" />
                        <span className=' absolute top-6 left-82'>{count}</span>
                        
                </div>

                <div className="user-icon p-2">
                    <img className='' src={userIcon} alt="" />
                </div>
            </div>
        </nav>
        <CartModal count={count} setCounter={setCounter} showModal={showModal} setShowModal={setShowModal} />
  </div>;
}

export default Nav;

