import React, { useState } from 'react';
import ProductDetails from './productDetails';
import ProductSlide from './productSlide';


function Product() {
  let [count, setCounter] = useState(0)

    const CounterInc = () => {
            setCounter(count + 1)
            
    }
  
    const CounterDec = () => {
        setCounter(count - 1)
  }
  return <div className='flex mt-5 p-10'>
      <ProductSlide/>
      <ProductDetails count={count} setCounter={setCounter} CounterInc={CounterInc} 
      CounterDec={CounterInc}
      />
  </div>;
}

export default Product;
