import {React, useState} from 'react';

function Counter() {
    const [count, setCounter] = useState(0)

    const CounterInc = () => {
            setCounter(count++)
    }

    const CounterDec = () => {
        setCounter(count--)
}
  return <div className='flex h-8'>
        <div className='rounded w-1/5 flex justify-evenly shadow h-10 bg-Lightgrayishblue font-bold text-center text-2xl'>
            <button className='text-Orange text-4xl' onClick={CounterInc}>-</button>
            <span className='' >{count}</span>
            <button className=' text-Orange' onClick={CounterDec} >+</button>
        </div>
        <div className='ml-5'>
            <button className='text-White bg-Orange text-center w-3/4 h-10 '>Add to cart</button>
        </div>
  </div>;
}

export default Counter;
