import React from 'react';
import thumbNail from '../images/image-product-1-thumbnail.jpg'


function CartModal({showModal, setShowModal, count, setCounter}) {
   const shoeDetails = [{
       id: 12,
       name: 'Autumn Limited Edition',
       discount: 50,
       price: 250
   }]

   const delCartItem = () => {
   console.log(shoeDetails)
   
   }

  return <>{
  showModal ? <div className='absolute z-50  md-right-24 h-56 right-20 bg-White p-5 shadow max-w-full md-w-64 divide-y'>
        <h2 className=''>Cart</h2>
        {count > 0 ? <div className='flex flex-col items-center justify-between p-2'>
            <div className="flex">
            <img className='h-16 w-12 rounded p-1' src={thumbNail} alt="" />
            <div className="text-Grayishblue">
                <p>{shoeDetails.name}</p>
                <p>{shoeDetails.discount/100 * shoeDetails.price} x {count} <span>{(shoeDetails.discount/100 * shoeDetails.price) * count}</span> <span>{'$'}</span></p>
            </div>
            <div>
            <button onClick={delCartItem} >del</button>
            </div>
            </div>
           <button className=' h-10 text-center cursor-pointer active:bg-Paleorange  shadow p-2 w-4/5 bg-Orange rounded text-White hover:bg-red cursor-pointer font-bold font-press-start'>Checkout</button>
        </div> 
        : <div className='flex justify-center items-center p-16
        '><p className='text-center max-w-full'>Your Cart is Empty</p></div>}
        </div> : 
    null}
  </>
}

export default CartModal;
