import Counter from './Counter';

function ProductDetails({count, setCounter, CounterDec, CounterInc}) {

  
  
  return <div className='md:w-1/2 w-full flex flex-col md:space-y-10 md:p-10 font-press-start'>
      <div className="details">
      <h1 className='text-Orange font-bold mb-5'>SNEAKERS COMPANY</h1>
      <h2 className='font-bold text-3xl md:text-6xl leading-12 mb-5 leading-12'>Fall Limited Edition Sneakers</h2>

      <p className='text-Grayishblue text-xl font-medium  leading-15 w-4/5 '>These low-profile sneakers are your perfect casual wear companion. Featuring a 
        durable rubber outer sole, theyâ€™ll withstand everything the weather can offer.</p>
      </div>

      <div className="price">
          <p className='font-bold text-xl'>$125.00 <span className='h-3 text-Orange bg-Paleorange text-center w-2 rounded '>50%</span> </p>
          <p className='text-Grayishblue line-through'>$250</p>
      </div>
      <Counter count={count} setCounter={setCounter} CounterDec={CounterDec} CounterInc={CounterInc} />
    
  </div>;
}

export default ProductDetails;
