import {React, useState} from 'react';
import productOne from '../images/image-product-1.jpg'
import productTwo from '../images/image-product-2.jpg'
import productThree from '../images/image-product-3.jpg'
import productFour from '../images/image-product-4.jpg'

import { Navigation, Thumbs} from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';

// Import Swiper styles
import 'swiper/css';
import 'swiper/css/navigation';
import SlideThumbs from './SliderThumbs';


function ProductSlide() {
  const [thumbsSwiper, setThumbsSwiper] = useState(null);

  return <div className='w-1/2'>
       <Swiper
      // install Swiper modules
      modules={[Navigation, Thumbs]}
      // navigation
      thumbs={{ swiper: thumbsSwiper }}
  
    >
      <SwiperSlide className='' >
        <img className='rounded w-4/5 h-70' src={productOne} alt="" />
      </SwiperSlide>
      <SwiperSlide>
        <img className='rounded w-4/5' src={productTwo} alt="" />
      </SwiperSlide>
      <SwiperSlide>
        <img className='rounded w-4/5' src={productThree} alt="" />
      </SwiperSlide>
      <SwiperSlide>
        <img className='rounded w-4/5' src={productFour} alt="" />
      </SwiperSlide>
    </Swiper>

    <Swiper
        modules={[Thumbs]}
        watchSlidesProgress
        onSwiper={setThumbsSwiper}
        slidesPerView={4}
        spaceBetween={3}
      >
        <SwiperSlide >
        <img className='rounded' src={productOne} alt="" />
        </SwiperSlide>

        <SwiperSlide >
        <img className='rounded' src={productTwo} alt="" />
        </SwiperSlide>

        <SwiperSlide >
        <img className='rounded' src={productThree} alt="" />
        </SwiperSlide>

        <SwiperSlide >
        <img className='rounded' src={productFour} alt="" />
        </SwiperSlide>
      </Swiper>

  </div>;
}

export default ProductSlide;
