import {React, useState} from 'react';
import Counter from './Counter';

function ProductDetails() {
  let [count, setCounter] = useState(0)

    const CounterInc = () => {
            setCounter(count + 1)
            
    }
  
    const CounterDec = () => {
        setCounter(count - 1)
  }
  return <div className='w-1/2 flex flex-col space-y-10 p-10 font-press-start'>
      <div className="details">
      <h1 className='text-Orange font-bold mb-5'>SNEAKERS COMPANY</h1>
      <h2 className='font-bold text-6xl leading-20 mb-5 leading-12'>Fall Limited Edition Sneakers</h2>

      <p className='text-Grayishblue text-xl font-semibold  leading-15 w-4/5 '>These low-profile sneakers are your perfect casual wear companion. Featuring a 
        durable rubber outer sole, theyâ€™ll withstand everything the weather can offer.</p>
      </div>

      <div className="price">
          <p className='font-bold text-xl'>$125.00 <span className='h-3 text-Orange bg-Paleorange text-center w-2 rounded '>50%</span> </p>
          <p className='text-Grayishblue line-through'>$250</p>
      </div>
      <Counter count={count} setCounter={setCounter} CounterInc={CounterInc} 
      CounterDec={CounterDec} />
    
  </div>;
}

export default ProductDetails;
