import {React, useState} from 'react';

function Counter() {
    let [count, setCounter] = useState(0)

    const CounterInc = () => {
            setCounter(count + 1)
            
    }
  
    const CounterDec = () => {
        setCounter(count - 1)
  }

  return <div className='flex h-8 text-center'>
        <div className='rounded w-1/5  flex justify-evenly items-center shadow h-10 bg-Lightgrayishblue font-bold text-center'>
            <button className='text-Orange text-4xl active:text-Paleorange' onClick={CounterDec}>-</button>
            <span className='' >{count}</span>
            <button className=' text-Orange text-3xl' onClick={CounterInc} >+</button>
        </div>
        <div className='ml-5 h-10 text-center cursor-pointer active:bg-Paleorange  shadow p-2 w-3/5 bg-Orange rounded'>
            <button className='text-White hover:bg-red cursor-pointer font-bold font-press-start  '>Add to cart</button>
        </div>
  </div>;
}

export default Counter;

