import React from 'react';
import thumbNail from '../images/image-product-1-thumbnail.jpg'
import CartItem from './CartItem';


function CartModal({showModal, setShowModal, count, setCounter}) {
   const shoeDetails = [{
       id: 2,
       name: 'Autumn Limited Edition',
       discount: 50,
       price: 250
   }]

   const delCartItem = (index) => {
    shoeDetails.splice(index, 1)

   }

  return <>{
  showModal ?  shoeDetails.map((e) => {
      return <CartItem count={count} key={e.id}  delCartItem={delCartItem} discount={e.discount} price={e.price} shoename={e.name} id={e.id}  />
  }) : 
 null}
  </>
}

export default CartModal;
