import React from 'react';
import thumbNail from '../images/image-product-1-thumbnail.jpg'


function CartModal({showModal, setShowModal}) {
    // const cartCalulator = () => {
    //     if(count > 0){
    //         setCounter(price * count)
    //     }
    // }
    let count = 1;
  return <>{
  showModal ? <div className='absolute right-24 h-48 bg-White p-5 shadow w-64 divide-y'>
        <h2 className=''>Cart</h2>
        {count > 0 ? <div className='flex flex-col'>
            <div className="flex">
            <img className='h-16 w-12 rounded p-1' src={thumbNail} alt="" />
            <div className="text-Grayishblue">
                <p>Product Name</p>
                <p>Price x <span>Quantity</span> <span>Total</span></p>
            </div>
            <div>
            <button>del</button>
            </div>
            </div>
           <button lassName='text-White hover:bg-red cursor-pointer font-bold font-press-start'>Checkout</button>
        </div> :<p className='text-center'>Your Cart is Empty</p>}
  </div> : null}
  </>
}

export default CartModal;
